const updown = (state = 0,action) => {
    switch(action.type)
    {
        case  "INCREMENT":
            return state + 1;
        break;

        case  "DECREMENT":
            return state - 1;
        break;

        default : 
            return state = 0;

    }
} 
export default updown;
