import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { incNum,decNum } from "./action";

const App = () => {

    const num =  useSelector(state => state.CheckNumber);
    const dispatch = useDispatch();

    return (
        <>
           <button>+</button>
           <input type="text" onClick={()=>dispatch(incNum())} value={num}/>
           <button>-</button>
        </>
    )
}

export default App;
